  <button onclick="rollDice()" style="margin-top: 20px; padding: 10px 20px; font-size: 18px;">Jogar Dado</button>



<script>
  const circles = Array.from(document.querySelectorAll('circle'))
    .filter(c => c.getAttribute('fill') === '#FB1D1D');

  const player = document.getElementById('player');
  let currentIndex = 0;

  // Colocar o player na primeira casa
  function placePlayer(index) {
    const target = circles[index];
    const x = parseFloat(target.getAttribute('cx')) || 0;
    const y = parseFloat(target.getAttribute('cy')) || 0;
    const transform = target.getAttribute('transform');

    // Se o círculo tem transform (ex: matrix), aplicamos também
    if (transform) {
      const match = /translate\(([^)]+)\)/.exec(transform);
      if (match) {
        const [tx, ty] = match[1].split(',').map(parseFloat);
        player.setAttribute('cx', x + tx);
        player.setAttribute('cy', y + ty);
        return;
      }

      // Alternativamente, parseamos matrix
      const matrixMatch = /matrix\(([^)]+)\)/.exec(transform);
      if (matrixMatch) {
        const matrixValues = matrixMatch[1].split(' ').map(parseFloat);
        const tx = matrixValues[4];
        const ty = matrixValues[5];
        player.setAttribute('cx', x + tx);
        player.setAttribute('cy', y + ty);
        return;
      }
    }

    // Sem transform
    player.setAttribute('cx', x);
    player.setAttribute('cy', y);
  }

  // Movimento do player
  function movePlayer(steps) {
    const total = circles.length;
    let targetIndex = currentIndex + steps;

    if (targetIndex >= total) {
      targetIndex = total - 1; // Para no final
    }

    let step = 0;
    const interval = setInterval(() => {
      if (step >= steps || currentIndex >= total - 1) {
        clearInterval(interval);
        return;
      }
      currentIndex++;
      placePlayer(currentIndex);
      step++;
    }, 300); // tempo entre movimentos
  }

  // Joga o dado
  function rollDice() {
    const roll = Math.floor(Math.random() * 6) + 1;
    alert('Você rolou: ' + roll);
    movePlayer(roll);
  }

  // Inicializa o jogador na primeira casa
  placePlayer(currentIndex);
</script>

  
